$(document).ready(function() {
    mobile(), cryptoCarousel(), effect(), form();
}), $(window).on("scroll", function() {
    form();
}), $(window).on("resize", function() {
    mobile(), cryptoCarouselNav(), form();
});

var mobile = function() {
    window.matchMedia("(max-width: 767px)").matches ? ($("body").addClass("mobile"), 
    $(".body-wrapper").removeClass("fixed")) : $("body").removeClass("mobile");
}, cryptoCarousel = function() {
    $(".crypto-carousel").owlCarousel({
        loop: !1,
        margin: 10,
        items: 7,
        nav: !0,
        dots: !0,
        navText: [ "" ],
        navSpeed: 600,
        dragEndSpeed: 600,
        autoHeight: !0,
        responsiveRefreshRate: 0,
        responsive: {
            0: {
                items: 1,
                dots: !1
            },
            575: {
                items: 2
            },
            768: {
                items: 3
            },
            992: {
                items: 4
            },
            1200: {
                items: 5
            }
        }
    }), cryptoCarouselNav();
}, cryptoCarouselNav = function() {
    $(".owl-next").css({
        marginLeft: 22.5 * $(".owl-dots").children(".owl-dot").length
    });
}, parallax = function() {
    scTop = $(window).scrollTop(), wH = $(window).height(), $(".parallax-top").each(function() {
        $(this).css("top", "0");
        var a = $(this).attr("data-parallax"), b = $(this).offset().top, c = b - wH, d = c - scTop;
        scTop, $(this).css("top", d / a);
    });
}, effect = function() {
    wow = new WOW({
        boxClass: "eff",
        animateClass: "animated",
        offset: 0,
        mobile: !0,
        live: !0
    }), wow.init(), $(".clickform").on("click", function() {
        return window.matchMedia("(max-width: 991px)").matches ? (wh = $(window).height(), 
        ofTop = $(".form-wrapper").offset().top, ofHeight = $(".form-wrapper").outerHeight(), 
        val = wh - ofHeight, ofTop -= val, $("html, body").animate({
            scrollTop: ofTop
        }, 800, function() {
            $(".banner-wrapper").prepend("<div class='down-animate fadeIn animated' title='Kapat'><span class='slideInDownRep animated'></span></div>");
        })) : $("body").prepend("<div class='down-animate fadeIn animated' title='Kapat'><span class='slideInDownRep animated'></span></div>"), 
        !1;
    }), $("body").on("click touchstart", function() {
        0 != !$(".form-wrapper:hover").length && ($(".down-animate").addClass("fadeOut"), 
        setTimeout(function() {
            $(".down-animate").remove();
        }, 700));
    });
}, form = function() {
    $(window).scrollTop() > 0 ? ($(".form-wrapper").addClass("bottom-active"), $(".form-wrapper .form-control").on("focus", function() {
        window.matchMedia("(max-width: 991px)").matches || ($(".checkbox-wrapper").removeClass("d-lg-none").addClass("d-flex animated fadeInUp"), 
        $("footer").addClass("opform"));
    })) : ($(".form-wrapper").removeClass("bottom-active"), $(".down-animate").remove()), 
    window.matchMedia("(max-width: 1400px)").matches && $(".form-wrapper").addClass("bottom-active");
};